import React, { Component } from 'react'
import { Chart, Line, Axis } from 'bizcharts';
import WeatherForeCast from '../WeatherForeCast/WeatherForeCast'
import WeatherIcon from "../WeatherIcon/WeatherIcon";
import * as styles from './WeatherForm.module.css'



class WeatherForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cityName: this.props.cityName,
      wealtherInfo: this.props.wealtherInfo,
      currentDate: this.props.currentDate,
      currentHighestTemp: this.props.wealtherInfo[3].main.temp,
      currentWeatherData: this.props.currentWeatherData,
      iconType: this.props.iconType,
      iconName: null
    };
  }
  render() {
    return (
      <div className="container-fluid mx-1 h-75 w-100">
        <div className="container-fluid">
          <h1 className="text-left text-light">{this.state.cityName}</h1>
          <h2 className="text-left text-light">{this.state.currentDate}</h2>
        </div>
        <div className={styles.currentWeatherWrapper}>
          <WeatherIcon iconType={this.state.iconType}/>
          <h1 className={styles.currentTempature}>
            {this.state.currentHighestTemp}
          </h1>
          <div className={styles.weatherTable}>
            <Chart scale={{temperature: {min: 0}}} 
              padding={[30,20,50,40]} 
              autoFit 
              height={320} 
              data={this.state.currentWeatherData}>
              <Line shape="smooth" 
                position="hour*temperature" 
                color="city" 
                label="temperature"/>
            </Chart>
          </div>
        </div>
        <div className="container-fluid w-100 pt-5 d-flex">
          <WeatherForeCast />
        </div>
      </div>
    );
  }
}

export default WeatherForm;
