import React, { Component } from 'react'

class HeaderComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      receivedData: this.props.receivedData
    };
  }
  render() {
    return (
      <>
        {this.state.receivedData 
        && <h3 className="col-md-l ml-3 text-light">WEATHER APP</h3>}
        <div className="container-fluid h-20 w-100">
          {!this.state.receivedData 
          && <h1 className="display-3 text-light">WEATHER APP</h1>}
        </div>
      </>
    );
  }
}

export default HeaderComponent;
