import React, { Component } from 'react'

class ErrorMessage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      message: this.props.message
    };
  }
  render() {
    return (
      <div className="alert alert-warning">{this.state.message}</div>
    );
  }
}

export default ErrorMessage;
