import React, { Component } from 'react'
import moment from 'moment'

import WeatherForm from '../WeatherForm/WeatherForm'
import SearchWeatherByCity from '../SearchWeatherByCity/SearchWeatherByCity'
import WeatherAppService from "../../APIs/WeatherDataAPI/WeatherAppService";
class WealtherApp extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cityName: '',
      currentDate: moment(new Date()).format('YYYY-MM-DD'),
      currentWeatherData: [],
      receivedData: false,
      wealtherInfo: []
    };
    this.searchClickedHandler = this.searchClickedHandler.bind(this);
  }

  searchClickedHandler = (cityName) => {
    this.refreshWeatherData(cityName);
  }

  refreshWeatherData = (cityName) => {
    WeatherAppService.retrieveLocalWeather(cityName)
      .then(response => {
        let currentWeather = [];
        response.data.list.map( day => {
            currentWeather.push({
              hour: moment(day.dt_txt).format('HH'),
              city: cityName,
              temperature: day.main.temp
            })

        })
        console.log("success")
        this.setState({currentWeatherData: [...currentWeather]});
        this.setState({wealtherInfo: [...response.data.list]});

      });
  }

  render() {
    return (
      <div className="container-fluid h-100 w-100 row align-items-center">
        <div className="container-fluid h-20 w-100">
        {this.state.receivedData 
        && <h5 className="text-left ml-4 text-light">WEATHER APP</h5>}
        {!this.state.receivedData 
        && <h1 className="display-3 text-light">WEATHER APP</h1>}
        <SearchWeatherByCity  searchClickedHandler={this.searchClickedHandler}/>
        </div>
          {this.state.receivedData
          && <WeatherForm 
            cityName={this.state.cityName}
            currentDate={this.state.currentDate}
            currentWeatherData={this.state.currentWeatherData}
            wealtherInfo={this.state.wealtherInfo}

          />}
      </div>
    );
  }
}

export default WealtherApp;
