import React, { Component } from 'react'

class WealtherApp extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cityName: '',
      wealtherInfo: null
    };
    this.searchCityHandler = this.searchCityHandler.bind(this);
  }
  searchCityHandler = (value) => {
    console.log(value);
  }
  render() {
    return (
      <div className="container">
        <h1 className="display-3">WEATHER APP</h1>
        <input type="text" placeholder="Enter city" value={this.state.cityName} onClick={this.searchCityHandler} />
      </div>
    );
  }
}

export default WealtherApp;
