import React, { Component } from 'react'
import moment from 'moment'
import logo from '../../../logo.svg'

class ForCastComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      pastDay: this.props.pastDay
    };
  }
  render() {
    return (
      <button className="btn mx-1" style={{width: "14rem"}}>
          <img className="card-img-top " src={logo} alt="Card image cap" />
          <p className="card-text">{moment().add(this.state.pastDay,'days').format('dddd')}</p>
          <p className="card-text">{moment().add(this.state.pastDay,'days').format('MMM Do')}</p>
          Click
      </button> 
    );
  }
}

export default ForCastComponent;
