import React, { Component } from 'react'

import ForCastComponent from './ForeCastComponent/ForCastComponent'
class WeatherForeCast extends Component {
  constructor(props) {
    super(props);
    this.state = {
      pastDay:[-7,-5,-5,-4,-3,-2,-1]
    };
  }
  render() {
    return (
      <>
        {this.state.pastDay.map( (day)=> (
          <ForCastComponent pastDay={day}/>
        ))}
      </>
    );
  }
}

export default WeatherForeCast;
