import React, { Component } from 'react'
import moment from 'moment'
import * as styles from './WeatherApp.module.css'
import { Chart, Line, Point } from 'bizcharts';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faCloud,
  faBolt,
  faCloudRain,
  faCloudShowersHeavy,
  faSnowflake,
  faSun,
  faSmog,
} from '@fortawesome/free-solid-svg-icons';

const data = [
  {
      month: "Jan",
      city: "Tokyo",
      temperature: 7
  },
  {
      month: "Jan",
      city: "London",
      temperature: 3.9
  },
  {
      month: "Feb",
      city: "Tokyo",
      temperature: 6.9
  },
  {
      month: "Feb",
      city: "London",
      temperature: 4.2
  },
  {
      month: "Mar",
      city: "Tokyo",
      temperature: 9.5
  },
  {
      month: "Mar",
      city: "London",
      temperature: 5.7
  },
  {
      month: "Apr",
      city: "Tokyo",
      temperature: 14.5
  },
  {
      month: "Apr",
      city: "London",
      temperature: 8.5
  },
  {
      month: "May",
      city: "Tokyo",
      temperature: 18.4
  },
  {
      month: "May",
      city: "London",
      temperature: 11.9
  },
  {
      month: "Jun",
      city: "Tokyo",
      temperature: 21.5
  },
  {
      month: "Jun",
      city: "London",
      temperature: 15.2
  },
  {
      month: "Jul",
      city: "Tokyo",
      temperature: 25.2
  },
  {
      month: "Jul",
      city: "London",
      temperature: 17
  },
  {
      month: "Aug",
      city: "Tokyo",
      temperature: 26.5
  },
  {
      month: "Aug",
      city: "London",
      temperature: 16.6
  },
  {
      month: "Sep",
      city: "Tokyo",
      temperature: 23.3
  },
  {
      month: "Sep",
      city: "London",
      temperature: 14.2
  },
  {
      month: "Oct",
      city: "Tokyo",
      temperature: 18.3
  },
  {
      month: "Oct",
      city: "London",
      temperature: 10.3
  },
  {
      month: "Nov",
      city: "Tokyo",
      temperature: 13.9
  },
  {
      month: "Nov",
      city: "London",
      temperature: 6.6
  },
  {
      month: "Dec",
      city: "Tokyo",
      temperature: 9.6
  },
  {
      month: "Dec",
      city: "London",
      temperature: 4.8
  }
];



class WealtherApp extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cityName: 'Irvine',
      currentDate: moment(new Date()).format('YYYY-MM-DD'),
      receivedData: true,
      wealtherInfo: [{
        tempature: 18
      }]
    };
    this.searchCityHandler = this.searchCityHandler.bind(this);
    this.searchClickedHandler = this.searchClickedHandler.bind(this);
  }
  searchCityHandler = (event) => {
    this.setState({
      [event.target.name]: event.target.value
    });
    console.log(event.target.value)
  }
  searchClickedHandler = () => {
    console.log("tt")
    this.setState({receivedData: !this.state.receivedData})
  }
  render() {
    return (
      <>
        <div className="container h-25">
          <h1 className="display-3">WEATHER APP</h1>
          <input 
                type="text" 
                name="cityName" 
                value={this.state.username} 
                placeholder="Enter a City Name:"
                onChange={this.searchCityHandler} /> 
          <button onClick={this.searchClickedHandler}>Search</button>
        </div>

          {this.state.receivedData
            && 
            <div className="container h-75">
              <div className={styles.location}>
                <h1 className="text-left">{this.state.cityName}</h1>
                <h2 className="text-left">{this.state.currentDate}</h2>
              </div>
              <div className={styles.currentWeatherWrapper}>
                {/* <h1>天气 logo</h1> */}
                <FontAwesomeIcon icon={faSun} size="6x"/>
                <h1 className={styles.currentTempature}>{this.state.wealtherInfo[0].tempature}</h1>
                <div className={styles.weatherTable}>
                  <Chart scale={{temperature: {min: 0}}} padding={[30,20,50,40]} autoFit height={320} data={data} >
                    <Line shape="smooth" position="month*temperature" color="city" label="temperature"/>
                    <Point position="month*temperature" color="city" /> 
                  </Chart>
                </div>
              </div>
              <div>
                <span className={styles.weatherBox}>Box1</span> 
                <span className={styles.weatherBox}>Box2</span>
                <span className={styles.weatherBox}>Box3</span>
                <span className={styles.weatherBox}>Box4</span>
                <span className={styles.weatherBox}>Box5</span>
                <span className={styles.weatherBox}>Box6</span>
                <span className={styles.weatherBox}>Box7</span>
              </div>
            </div>
          }
      </>
    );
  }
}

export default WealtherApp;
