import React, { Component } from 'react'
import moment from 'moment'
import WeatherData from '../WeatherData/WeatherData'

class WealtherApp extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cityName: '',
      currentDate: moment(new Date()).format('YYYY-MM-DD'),
      currentWeatherData: [
        {
          hour: "0",
          city: "Tokyo",
          temperature: 7
        },
        {
          hour: "1",
            city: "Tokyo",
            temperature: 6.9
        },
        {
          hour: "2",
          city: "Tokyo",
          temperature: 9.5
        },
        {
          hour: "3",
          city: "Tokyo",
          temperature: 8.5
        },
        {
          hour: "4",
          city: "Tokyo",
          temperature: 11.9
        },
        {
          hour: "5",
          city: "Tokyo",
          temperature: 25.2
        },
        {
          hour: "6",
          city: "Tokyo",
          temperature: 16.6
        },
        {
          hour: "7",
          city: "Tokyo",
          temperature: 23.3
        },
        {
          hour: "8",
          city: "Tokyo",
          temperature: 18.3
        },
        {
          hour: "9",
          city: "Tokyo",
          temperature: 13.9
        },
        {
          hour: "10",
          city: "Tokyo",
          temperature: 9.6
        }
      ],
      receivedData: false,
      wealtherInfo: [{
        tempature: 18
      }]
    };
    this.searchCityHandler = this.searchCityHandler.bind(this);
    this.searchClickedHandler = this.searchClickedHandler.bind(this);
  }
  searchCityHandler = (event) => {
    this.setState({
      [event.target.name]: event.target.value
    });
    console.log(event.target.value)
  }
  searchClickedHandler = () => {
    this.setState({receivedData: !this.state.receivedData})
  }
  render() {
    return (
      <div className="container-fluid h-100 w-100 row align-items-center">
        {this.state.receivedData && <h3 className="col-md-l ml-3">WEATHER APP</h3>}
        <div className="container-fluid h-20 w-100">
        {!this.state.receivedData && <h1 className="display-3">WEATHER APP</h1>}
          <input 
                type="text" 
                name="cityName" 
                value={this.state.username} 
                placeholder="Enter a City Name:"
                onChange={this.searchCityHandler} /> 
          <button onClick={this.searchClickedHandler}>Search</button>
        </div>

          {this.state.receivedData
            && 
            <WeatherData 
              cityName={this.state.cityName}
              currentDate={this.state.currentDate}
              currentWeatherData={this.state.currentWeatherData}/>
          }
      </div>
    );
  }
}

export default WealtherApp;
