import React, { Component } from 'react'

class WeatherIcon extends Component {
  constructor(props) {
    super(props);
    this.state = {
      iconType: this.props.iconType,
      iconName: null
    };
  }
  componentDidMount = () => {
    switch(this.state.iconType) {
      case "Clear":
        this.setState({iconName: sun});
        break;
      case "Rain":
        this.setState({iconName: rain});
        break;
      case "Clouds":
        this.setState({iconName: cloud});
        break;
      case "Drizzle":
        this.setState({iconName: drizzle});
        break;
      case "Snow":
        this.setState({iconName: snow});
        break;
      case "Thunderstorm":
        this.setState({iconName: thunder});
        break;
      default:
        this.setState({iconName: sun});
    }
  }
  render() {
    return (
      <img src={this.state.iconName} className="h-100" />
    );
  }
}

export default WeatherIcon;
