import React, { Component } from 'react'
import moment from 'moment'
import axios from 'axios';
import WeatherAppService from '../../APIs/WeatherDataAPI/WeatherAppService'
import * as styles from './SearchWeatherByCity.module.css';

class SearchWeatherByCity extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cityName: "",
      currentWeatherData: [],
      weatherDater:[],
      error: null,
      goback: this.props.goback
    };
    this.searchCityHandler = this.searchCityHandler.bind(this);
    this.searchClickedHandler = this.searchClickedHandler.bind(this);
    this.validateInputHandler = this.validateInputHandler.bind(this);
  }

  searchCityHandler = (event) => {
    this.setState({
      [event.target.name]: event.target.value
    });
  }

  validateInputHandler = () => {
    if (this.state.cityName.length === 0) {
      return false;
    }
    return true;
  }

  searchClickedHandler = () => {
    if ( !this.validateInputHandler() ) {
        this.setState({error: "The city name cannot be emtpy"});
        return;
    } 
    else {
      console.log(this.state.cityName)
      // WeatherAppService.retrieveLocalWeather(this.state.cityName)
      // .then(response => {
      //   let currentWeather = [];
      //   response.data.list.map( day => {
      //       currentWeather.push({
      //         hour: moment(day.dt_txt).format('HH'),
      //         city: this.state.cityName,
      //         temperature: day.main.temp
      //       })
      //   })
      // this.setState({currentWeatherData: [...currentWeather]});
      // this.setState({weatherDater: [...response.data.list]});
      // this.setState({error: null});
      // }).then( () => {
      //   this.props.searchClickedHandler(this.state.cityName, this.state.weatherDater, this.state.currentWeatherData);
      // });
      this.props.searchClickedHandler(this.state.cityName);

      // this.refreshWeatherData(this.state.cityName);
    }
  }

  render() {
    return (
      <>
        {this.props.goback && <input 
          type="text" 
          name="cityName" 
          className={styles.search}
          value={this.state.cityName} 
          placeholder="Enter a City Name:"
          onChange={this.searchCityHandler} />}
        <button 
          className={styles.button} 
          onClick={this.searchClickedHandler}>Search
        </button>
        {this.state.error != null 
        && <div className="alert alert-warning">{this.state.error}</div>}
      </>
    );
  }
}

export default SearchWeatherByCity;
