import React, { Component } from 'react'
import moment from 'moment'
import { Chart, Line, Point } from 'bizcharts';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import logo from '../../../logo.svg'
import * as styles from './WeatherData.module.css'
import {
  faCloud,
  faBolt,
  faCloudRain,
  faCloudShowersHeavy,
  faSnowflake,
  faSun,
  faSmog,
} from '@fortawesome/free-solid-svg-icons';

class WeatherData extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cityName: this.props.cityName,
      currentDate: this.props.currentDate,
      currentHighestTemp: 26,
      currentWeatherData: this.props.currentWeatherData
    };
  }

  render() {
    return (
      <div className="container-fluid mx-1 h-75 w-100">
        <div className={styles.location}>
          <h1 className="text-left">{this.state.cityName}</h1>
          <h2 className="text-left">{this.state.currentDate}</h2>
        </div>

        <div className={styles.currentWeatherWrapper}>
          <FontAwesomeIcon icon={faSun} size="6x"/>
          <h1 className={styles.currentTempature}>
            {this.state.currentHighestTemp}</h1>
          <div className={styles.weatherTable}>
            <Chart scale={{temperature: {min: 0}}} padding={[30,20,50,40]} autoFit height={320} data={this.state.currentWeatherData} >
              <Line shape="smooth" position="month*temperature" color="city" label="temperature"/>
              <Point position="month*temperature" color="city" /> 
            </Chart>
          </div>
        </div>

        <div className="container-fluid w-100">
          <div className="card" style={{width: "13rem"}}>
            <img class="card-img-top " src={logo} alt="Card image cap" />
            <div class="card-body">
              <p class="card-text">{moment().add(-1,'days').format('MM-DD').toString()}</p>
            </div>
          </div>    


          <span className={styles.weatherBox}>Box2</span>
          <span className={styles.weatherBox}>Box3</span>
          <span className={styles.weatherBox}>Box4</span>
          <span className={styles.weatherBox}>Box5</span>
          <span className={styles.weatherBox}>Box6</span>
          <span className={styles.weatherBox}>Box7</span>
        </div>
      </div>
    );
  }
}

export default WeatherData;
