import React, { Component } from 'react'
import { Chart, Line, Point } from 'bizcharts';

import WeatherForeCast from '../WeatherForeCast/WeatherForeCast'

import sun from '../../assets/images/sun.png'
import * as styles from './WeatherForm.module.css'


class WeatherData extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cityName: this.props.cityName,
      wealtherInfo: this.props.wealtherInfo,
      currentTempature: [],
      currentHighestTemp: this.props.wealtherInfo[0].main.temp,
      currentWeatherData: this.props.currentWeatherData
    };
  }

  componentDidMount () {
    let currentWeather = [];
    this.state.wealtherInfo.map( day => {
      currentWeather.push({
        hour: day.dt_txt,
        city: this.state.cityName,
        temperature: day.main.temp
      })
    })
    console.log(currentWeather)
    this.setState({currentTempature: [...currentWeather]})
  }


  render() {
    return (
      <div className="container-fluid mx-1 h-75 w-100">
        <div className="container-fluid">
          <h1 className="text-left text-light">{this.state.cityName}</h1>
          <h2 className="text-left text-light">{this.state.currentDate}</h2>
        </div>

        <div className={styles.currentWeatherWrapper}>
          <img src={sun} className="h-50" />
          <h1 className={styles.currentTempature}>
            {this.state.currentHighestTemp}
          </h1>


          <div className={styles.weatherTable}>
            <Chart scale={{temperature: {min: 0}}} padding={[30,20,50,40]} autoFit height={320} data={this.state.currentWeatherData}  >
              <Line shape="smooth" position="temperature" color="city" label="temperature"  />
            </Chart>
            {/* <div className="btn-group">
            </div> */}
          </div>
        </div>

        <div className="container-fluid w-100 pt-5 d-flex">
          <WeatherForeCast />
        </div>
      </div>
    );
  }
}

export default WeatherData;
