import React, { Component } from 'react'
import moment from 'moment'
import WeatherData from './WeatherData/WeatherData'

class WealtherApp extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cityName: 'Irvine',
      currentDate: moment(new Date()).format('YYYY-MM-DD'),
      currentWeatherData: [
        {
            hour: "Jan",
            city: "Tokyo",
            temperature: 7
        },
        {
          hour: "Feb",
            city: "Tokyo",
            temperature: 6.9
        },
        {
            month: "Mar",
            city: "Tokyo",
            temperature: 9.5
        },
        {
            month: "Apr",
            city: "Tokyo",
            temperature: 8.5
        },
        {
            month: "May",
            city: "Tokyo",
            temperature: 11.9
        },
        {
            month: "Jul",
            city: "Tokyo",
            temperature: 25.2
        },
        {
            month: "Aug",
            city: "Tokyo",
            temperature: 16.6
        },
        {
            month: "Sep",
            city: "Tokyo",
            temperature: 23.3
        },
        {
            month: "Oct",
            city: "Tokyo",
            temperature: 18.3
        },
        {
            month: "Nov",
            city: "Tokyo",
            temperature: 13.9
        },
        {
            month: "Dec",
            city: "Tokyo",
            temperature: 9.6
        }
      ],
      receivedData: true,
      wealtherInfo: [{
        tempature: 18
      }]
    };
    this.searchCityHandler = this.searchCityHandler.bind(this);
    this.searchClickedHandler = this.searchClickedHandler.bind(this);
  }
  searchCityHandler = (event) => {
    this.setState({
      [event.target.name]: event.target.value
    });
    console.log(event.target.value)
  }
  searchClickedHandler = () => {
    this.setState({receivedData: !this.state.receivedData})
  }
  render() {
    return (
      <div className="container mx-1 h-100 w-100">
        <div className="container h-25 w-100">
          <h1 className="display-3">WEATHER APP</h1>
          <input 
                type="text" 
                name="cityName" 
                value={this.state.username} 
                placeholder="Enter a City Name:"
                onChange={this.searchCityHandler} /> 
          <button onClick={this.searchClickedHandler}>Search</button>
        </div>

          {this.state.receivedData
            && 
            <WeatherData 
              cityName={this.state.cityName}
              currentDate={this.state.currentDate}
              currentWeatherData={this.state.currentWeatherData}/>
          }
      </div>
    );
  }
}

export default WealtherApp;
